import fs from 'fs';
import path from 'path';
import {markdown} from './util.js';

export default class DocResolver {
  constructor(builder) {
    this._builder = builder;
    this._data = builder._data;
  }

  resolve() {
    this._resolveAccess();
    this._resolveOnlyExported();
    this._resolveIgnore();
    this._resolveMarkdown();
    this._resolveLink();
    this._resolveExtendsChain();
  }

  _resolveIgnore() {
    if (this._data.__RESOLVED_IGNORE__) return;

    let docs = this._builder._find({ignore: true});
    for (let doc of docs) {
      var regex = new RegExp(`^${doc.longname}[.~#]`);
      this._data({longname: {regex: regex}}).remove();
    }
    this._data({ignore: true}).remove();

    this._data.__RESOLVED_IGNORE__ = true;
  }

  _resolveAccess() {
    if (this._data.__RESOLVED_ACCESS__) return;

    let config = this._builder._config;
    let access = config.access || ['public', 'protected', 'private'];

    this._data().update(function(){
      if (!this.access) this.access = 'public';
      if (!access.includes(this.access)) this.ignore = true;
      return this;
    });

    this._data.__RESOLVED_ACCESS__ = true;
  }

  _resolveOnlyExported() {
    if (this._data.__RESOLVED_ONLY_EXPORTED__) return;

    let config = this._builder._config;
    let onlyExported = config.onlyExported;
    if (onlyExported) {
      this._data({export: false}).update({ignore: true});
    }

    this._data.__RESOLVED_ONLY_EXPORTED__ = true;
  }

  _resolveMarkdown() {
    if (this._data.__RESOLVED_MARKDOWN__) return;

    function convert(obj) {
      for (let key of Object.keys(obj)) {
        let value = obj[key];
        if (key === 'description' && typeof value === 'string') {
          obj[key] = markdown(value, true);
        } else if (typeof value === 'object' && value) {
          convert(value);
        }
      }
    }

    let docs = this._builder._find();
    for (let doc of docs) {
      convert(doc);
    }

    this._data.__RESOLVED_MARKDOWN__ = true;
  }

  _resolveLink() {
    if(this._data.__RESOLVED_LINK__) return;

    let link = (str)=>{
      if (!str) return str;

      return str.replace(/\{@link ([\w\#_\-.:\~\/]+)}/g, (str, longname)=>{
        return this._builder._buildDocLinkHTML(longname, longname);
      });
    };

    this._data().each((v)=>{
      v.description = link(v.description);

      if (v.params) {
        for (let param of v.params) {
          param.description = link(param.description);
        }
      }

      if (v.return) {
        v.return.description = link(v.return.description);
      }

      if (v.throws) {
        for (let _throw of v.throws) {
          _throw.description = link(_throw.description);
        }
      }

      if (v.see) {
        for (let i = 0; i < v.see.length; i++) {
          if (v.see[i].indexOf('{@link') === 0) {
            v.see[i] = link(v.see[i]);
          } else if(v.see[i].indexOf('<a href') === 0) {
            // ignore
          } else {
            v.see[i] = `<a href="${v.see[i]}">${v.see[i]}</a>`;
          }
        }
      }
    });

    this._data.__RESOLVED_LINK__ = true;
  }

  _resolveExtendsChain() {
    if (this._data.__RESOLVED_EXTENDS_CHAIN__) return;

    let extendsChain = (doc) => {
      if (!doc.extends) return;

      let selfDoc = doc;

      // traverse super class.
      let chains = [];
      while (1) {
        if (!doc.extends) break;

        let superClassDoc = this._builder._findByName(doc.extends[0])[0];
        if (superClassDoc) {
          chains.push(superClassDoc.longname);
          doc = superClassDoc;
        } else {
          chains.push(doc.extends[0]);
          break;
        }
      }

      if (chains.length) {
        // direct subclass
        let superClassDoc = this._builder._findByName(chains[0])[0];
        if (superClassDoc) {
          if (!superClassDoc._custom_direct_subclasses) superClassDoc._custom_direct_subclasses = [];
          superClassDoc._custom_direct_subclasses.push(selfDoc.longname);
        }

        // indirect subclass
        for (let superClassLongname of chains.slice(1)) {
          superClassDoc = this._builder._findByName(superClassLongname)[0];
          if (superClassDoc) {
            if (!superClassDoc._custom_indirect_subclasses) superClassDoc._custom_indirect_subclasses = [];
            superClassDoc._custom_indirect_subclasses.push(selfDoc.longname);
          }
        }

        // indirect implements and mixes
        for (let superClassLongname of chains) {
          superClassDoc = this._builder._findByName(superClassLongname)[0];
          if (!superClassDoc) continue;

          // indirect implements
          if (superClassDoc.implements) {
            if (!selfDoc._custom_indirect_implements) selfDoc._custom_indirect_implements = [];
            selfDoc._custom_indirect_implements.push(...superClassDoc.implements);
          }

          // indirect mixes
          //if (superClassDoc.mixes) {
          //  if (!selfDoc._custom_indirect_mixes) selfDoc._custom_indirect_mixes = [];
          //  selfDoc._custom_indirect_mixes.push(...superClassDoc.mixes);
          //}
        }

        // extends chains
        selfDoc._custom_extends_chains = chains.reverse();
      }
    };

    let implemented = (doc) =>{
      let selfDoc = doc;

      // direct implemented (like direct subclass)
      for (let superClassLongname of selfDoc.implements || []) {
        let superClassDoc = this._builder._findByName(superClassLongname)[0];
        if (!superClassDoc) continue;
        if(!superClassDoc._custom_direct_implemented) superClassDoc._custom_direct_implemented = [];
        superClassDoc._custom_direct_implemented.push(selfDoc.longname);
      }

      // indirect implemented (like indirect subclass)
      for (let superClassLongname of selfDoc._custom_indirect_implements || []) {
        let superClassDoc = this._builder._findByName(superClassLongname)[0];
        if (!superClassDoc) continue;
        if(!superClassDoc._custom_indirect_implemented) superClassDoc._custom_indirect_implemented = [];
        superClassDoc._custom_indirect_implemented.push(selfDoc.longname);
      }
    };

    //var mixed = (doc) =>{
    //  var selfDoc = doc;
    //
    //  // direct mixed (like direct subclass)
    //  for (var superClassLongname of selfDoc.mixes || []) {
    //    var superClassDoc = this._builder._find({longname: superClassLongname})[0];
    //    if (!superClassDoc) continue;
    //    if(!superClassDoc._custom_direct_mixed) superClassDoc._custom_direct_mixed = [];
    //    superClassDoc._custom_direct_mixed.push(selfDoc.longname);
    //  }
    //
    //  // indirect mixed (like indirect subclass)
    //  for (var superClassLongname of selfDoc._custom_indirect_mixes || []) {
    //    var superClassDoc = this._builder._find({longname: superClassLongname})[0];
    //    if (!superClassDoc) continue;
    //    if(!superClassDoc._custom_indirect_mixed) superClassDoc._custom_indirect_mixed = [];
    //    superClassDoc._custom_indirect_mixed.push(selfDoc.longname);
    //  }
    //};

    let docs = this._builder._find({kind: 'class'});
    for (let doc of docs) {
      extendsChain(doc);
      implemented(doc);
      //mixed(doc);
    }

    this._data.__RESOLVED_EXTENDS_CHAIN__ = true;
  }
}

import IceCap from 'ice-cap';
import DocBuilder from './DocBuilder.js';

export default class FileDocBuilder extends DocBuilder {
  exec(callback) {
    let ice = this._buildLayoutDoc();
    ice.autoClose = false;

    let docs = this._find({kind: 'file'});
    for (let doc of docs) {
      ice.load('content', this._buildFileDoc(doc), IceCap.MODE_WRITE);
      let fileName = this._getOutputFileName(doc);
      callback(ice.html, fileName);
    }
  }

  _buildFileDoc(doc) {
    let ice = new IceCap(this._readTemplate('@file.html'));
    ice.text('title', doc.longname);
    ice.load('content', doc.content);
    return ice.html;
  }
}

import fs from 'fs';
import IceCap from 'ice-cap';
import DocBuilder from './DocBuilder.js';

export default class IndexDocBuilder extends DocBuilder {
  exec(callback) {
    let ice = this._buildLayoutDoc();
    ice.load('content', this._buildIndexDoc());
    callback(ice.html, '@index.html');
  }

  _buildIndexDoc() {
    let indexInfo = this._getInfo();

    let ice = new IceCap(this._readTemplate('@index.html'));

    ice.text('title', indexInfo.title);
    ice.text('version', indexInfo.version, 'append');
    ice.text('url', indexInfo.url);
    ice.attr('url', 'href', indexInfo.url);
    ice.text('description', indexInfo.desc);

    ice.load('classSummary', this._buildSummaryHTML(null, 'class', 'Class Summary'), 'append');
    ice.load('interfaceSummary', this._buildSummaryHTML(null, 'interface', 'Interface Summary'), 'append');
    ice.load('functionSummary', this._buildSummaryHTML(null, 'function', 'Function Summary'), 'append');
    ice.load('variableSummary', this._buildSummaryHTML(null, 'variable', 'Variable Summary'), 'append');
    ice.load('typedefSummary', this._buildSummaryHTML(null, 'typedef', 'Typedef Summary'), 'append');
    ice.load('fileSummary', this._buildSummaryHTML(null, 'file', 'File Summary'), 'append');

    return ice;
  }
}

import fs from 'fs';
import IceCap from 'ice-cap';
import DocBuilder from './DocBuilder.js';
import {markdown} from './util.js';

export default class ReadmeDocBuilder extends DocBuilder {
  exec(callback) {
    let ice = this._buildLayoutDoc();
    ice.load('content', this._buildReadmeDoc());
    callback(ice.html, 'index.html');
  }

  _buildReadmeDoc() {
    if (!this._config.readme) return 'NO README';

    let readme = fs.readFileSync(this._config.readme, {encode: 'utf8'}).toString();

    let html = this._readTemplate('@readme.html');
    let ice = new IceCap(html);
    ice.load('readme', markdown(readme));

    return ice;
  }
}

import path from 'path';
import DocBuilder from './DocBuilder.js';

export default class SearchIndexBuilder extends DocBuilder {
  exec(callback) {
    let searchIndex = [];
    let docs = this._find({});

    for (let doc of docs) {
      let indexText, url, displayText;

      if (doc.importPath) {
        displayText = `<span>${doc.name}</span> <span class="search-result-import-path">${doc.importPath}</span>`;
        indexText = `${doc.importPath}~${doc.name}`.toLowerCase();
        url = this._getURL(doc, null, 2);
      } else {
        displayText = doc.longname;
        indexText = displayText.toLowerCase();
        url = this._getURL(doc, null, 2);
      }

      searchIndex.push([indexText, url, displayText]);
    }

    searchIndex.sort((a, b)=>{
      if (a[2] === b[2]) {
        return 0;
      } else if (a[2] < b[2]) {
        return -1;
      } else {
        return 1;
      }
    });

    let javascript = 'window.jsdocCloudySearchIndex = ' + JSON.stringify(searchIndex, null, 2);

    callback(javascript, 'script/search_index.js');
  }
}

import IceCap from 'ice-cap';
import DocBuilder from './DocBuilder.js';

export default class SingleDocBuilder extends DocBuilder {
  exec(callback) {
    let ice = this._buildLayoutDoc();
    ice.autoClose = false;

    let kinds = ['function', 'variable', 'typedef'];
    for (let kind of kinds) {
      let docs = this._find({kind: kind});
      if (!docs.length) return;
      ice.load('content', this._buildSingleDoc(kind), IceCap.MODE_WRITE);
      let fileName = this._getOutputFileName(docs[0]);
      callback(ice.html, fileName);
    }
  }

  _buildSingleDoc(kind) {
    let title = kind.replace(/^(\w)/, (c)=> c.toUpperCase() );
    let ice = new IceCap(this._readTemplate('@single.html'));
    ice.text('title', title);
    ice.load('summaries', this._buildSummaryHTML(null, kind, 'Summary'), 'append');
    ice.load('details', this._buildDetailHTML(null, kind, ''));
    return ice.html;
  }
}

import path from 'path';
import DocBuilder from './DocBuilder.js';

export default class StaticFileBuilder extends DocBuilder {
  exec(callback) {
    callback(path.resolve(__dirname, './template/css'), './css');
    callback(path.resolve(__dirname, './template/script'), './script');
    callback(path.resolve(__dirname, './template/image'), './image');

    // see DocBuilder#_buildLayoutDoc
    let scripts = this._config.scripts || [];
    for (let i = 0; i < scripts.length; i++) {
      let userScript = scripts[i];
      let name = `./user/script/${i}-${path.basename(userScript)}`;
      callback(userScript, name);
    }

    let styles = this._config.styles || [];
    for (let i = 0; i < styles.length; i++) {
      let userStyle = styles[i];
      let name = `./user/css/${i}-${path.basename(userStyle)}`;
      callback(userStyle, name);
    }
  }
}

import marked from 'marked';

export function shorten(doc) {
  if (!doc) return '';

  if (doc.summary) return doc.summary;

  let desc = doc.description;
  if (!desc) return '';

  let len = desc.length;
  let inSQuote = false;
  let inWQuote = false;
  let inCode = false;
  for (let i = 0; i < desc.length; i++) {
    let char1 = desc.charAt(i);
    let char2 = desc.charAt(i + 1);
    let char4 = desc.substr(i, 6);
    let char5 = desc.substr(i, 7);
    if (char1 === "'") inSQuote = !inSQuote;
    else if (char1 === '"') inWQuote = !inWQuote;
    else if (char4 === '<code>') inCode = true;
    else if (char5 === '</code>') inCode = false;

    if (inSQuote || inCode || inWQuote) continue;

    if (char1 === '.') {
      if (char2 === ' ' || char2 === '\n' || char2 === '<') {
        len = i + 1;
        break;
      }
    } else if (char1 === '\n') {
      len = i + 1;
      break;
    }
  }

  return desc.substr(0, len);
}

export function markdown(text, breaks = false) {
  let compiled = marked(text, {
    gfm: true,
    tables: true,
    breaks: breaks,
    highlight: function (code) {
      return `<pre class="source-code"><code class="prettyprint">${code}</code></pre>`;
    }
  });

  return compiled;
}

import fs from 'fs-extra';
import path from 'path';
import {taffy} from 'taffydb';
import StaticFileBuilder from './builder/StaticFileBuilder.js';
import IndexDocBuilder from './builder/IndexDocBuilder.js';
import ReadmeDocBuilder from './builder/ReadmeDocBuilder.js';
import ClassDocBuilder from './builder/ClassDocBuilder.js';
import SingleDocBuilder from './builder/SingleDocBuilder.js';
import FileDocBuilder from './builder/FileDocBuilder.js';
import SearchIndexBuilder from './builder/SearchIndexBuilder.js';

export default function publish(values, config) {
  let dumpPath = path.resolve(config.destination, 'dump.json');
  fs.outputFileSync(dumpPath, JSON.stringify(values, null, 2));

  let data = taffy(values);

  function writeHTML(html, fileName) {
    console.log(fileName);
    var filePath = path.resolve(config.destination, fileName);
    fs.outputFileSync(filePath, html, {encoding: 'utf8'});
  }

  function copy(srcPath, destPath) {
    console.log(destPath);
    fs.copySync(srcPath, path.resolve(config.destination, destPath));
  }

  new IndexDocBuilder(data, config).exec(writeHTML);
  new ReadmeDocBuilder(data, config).exec(writeHTML);
  new ClassDocBuilder(data, config).exec(writeHTML);
  new SingleDocBuilder(data, config).exec(writeHTML);
  new FileDocBuilder(data, config).exec(writeHTML);
  new StaticFileBuilder(data, config).exec(copy);
  new SearchIndexBuilder(data, config).exec(writeHTML);
};

import estraverse from 'estraverse';

let ESTRAVERSE_KEYS = {
  Super: []
};

export default class ASTUtil {
  static traverse(ast, callback) {
    estraverse.traverse(ast, {
      enter: function(node, parent) {
        callback.call(this, node, parent);
      },

      keys: ESTRAVERSE_KEYS
    });
  }

  static findPathInImportDeclaration(ast, name) {
    let path = null;

    estraverse.traverse(ast, {
      enter: function(node, parent) {
        if (node.type !== 'ImportDeclaration') return;

        for (let spec of node.specifiers) {
          let localName = spec.local.name;
          if (localName === name) {
            path = node.source.value;
            this.break();
          }
        }
      },

      keys: ESTRAVERSE_KEYS
    });

    return path;
  }
}

import path from 'path';
import assert from 'assert';

export default class PathResolver {
  constructor(inDirPath, filePath, packageName = null, mainFilePath = null, pathPrefix = '') {
    assert(inDirPath);
    assert(filePath);

    this._inDirPath = path.resolve(inDirPath);
    this._filePath = path.resolve(filePath);
    this._packageName = packageName;
    if (mainFilePath) {
      this._mainFilePath = path.resolve(mainFilePath);
    }
    this._pathPrefix = pathPrefix || '';

    if (this._pathPrefix) {
      this._prefixedFilePath = path.resolve(`${this._pathPrefix}${path.sep}${this.filePath}`);
    } else {
      this._prefixedFilePath = path.resolve(this.filePath);
    }
  }

  get importPath() {
    if (this._mainFilePath === this._prefixedFilePath) {
      return this._packageName;
    }

    let relativeFilePath = this.filePath;
    if (this._packageName) {
      return path.normalize(`${this._packageName}${path.sep}${this._pathPrefix}${path.sep}${relativeFilePath}`);
    } else {
      return `./${relativeFilePath}`;
    }
  }

  get fileFullPath() {
    return this._filePath;
  }

  get filePath() {
    let relativeFilePath = path.relative(path.dirname(this._inDirPath), this._filePath);
    return relativeFilePath;
  }

  resolve(relativePath) {
    let selfDirPath = path.dirname(this._filePath);
    let resolvedPath = path.resolve(selfDirPath, relativePath);
    let resolvedRelativePath = path.relative(path.dirname(this._inDirPath), resolvedPath);
    return resolvedRelativePath;
  }
}
